import React, { useMemo, useState } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useNotifications } from '../../contexts/NotificationContext';
import api from '../../utils/api';

const Profile = () => {
  const { user, updateUser } = useAuth();
  const { addNotification } = useNotifications();
  const [editing, setEditing] = useState(false);
  const [saving, setSaving] = useState(false);
  const initial = useMemo(() => ({
    first_name: user?.first_name || '',
    last_name: user?.last_name || '',
    email: user?.email || '',
    phone_number: user?.phone_number || ''
  }), [user]);
  const [form, setForm] = useState(initial);

  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((f) => ({ ...f, [name]: value }));
  };

  const computeChanges = (from, to) => {
    const changed = {};
    Object.keys(to).forEach((k) => {
      if ((from[k] || '') !== (to[k] || '')) changed[k] = { from: from[k] || '', to: to[k] || '' };
    });
    return changed;
  };

  const onCancel = () => {
    setForm(initial);
    setEditing(false);
  };

  const onSave = async (e) => {
    e.preventDefault();
    const changed = computeChanges(initial, form);
    if (Object.keys(changed).length === 0) {
      alert('No changes to save.');
      setEditing(false);
      return;
    }

    setSaving(true);
    try {
      // Try to persist to backend if endpoint exists; otherwise this will be a no-op mock.
      let updated = { ...user, ...form };
      try {
        const res = await api.put('/users/me', form);
        if (res?.data?.user) updated = res.data.user;
      } catch (_) {
        // Fallback to local update if backend route isn't available
      }

      updateUser(updated);

      // Notify admin immediately
      addNotification({
        type: 'member_profile_updated',
        title: 'Member Profile Updated',
        message: `${updated.first_name} ${updated.last_name} updated their profile details`,
        priority: 'medium',
        actionUrl: '/admin/members',
        metadata: {
          audience: 'admin',
          memberId: updated.id || updated.user_id,
          changedFields: changed
        }
      });

      alert('Profile updated successfully. Admin has been notified.');
      setEditing(false);
    } catch (err) {
      console.error('Error saving profile:', err);
      alert('Failed to update profile. Please try again.');
    } finally {
      setSaving(false);
    }
  };

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h1 className="mb-0">My Profile</h1>
        {!editing ? (
          <button className="btn btn-primary" onClick={() => setEditing(true)}>Edit Profile</button>
        ) : (
          <div className="d-flex gap-2">
            <button className="btn btn-secondary" onClick={onCancel} disabled={saving}>Cancel</button>
            <button className="btn btn-success" onClick={onSave} disabled={saving}>{saving ? 'Saving...' : 'Save Changes'}</button>
          </div>
        )}
      </div>

      <div className="card">
        <div className="card-body">
          <form onSubmit={onSave}>
            <div className="row">
              <div className="col-md-6">
                <div className="mb-3">
                  <label className="form-label">First Name</label>
                  <input
                    type="text"
                    className="form-control"
                    name="first_name"
                    value={form.first_name}
                    onChange={onChange}
                    readOnly={!editing}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Last Name</label>
                  <input
                    type="text"
                    className="form-control"
                    name="last_name"
                    value={form.last_name}
                    onChange={onChange}
                    readOnly={!editing}
                    required
                  />
                </div>
              </div>
              <div className="col-md-6">
                <div className="mb-3">
                  <label className="form-label">Email</label>
                  <input
                    type="email"
                    className="form-control"
                    name="email"
                    value={form.email}
                    onChange={onChange}
                    readOnly={!editing}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Phone Number</label>
                  <input
                    type="tel"
                    className="form-control"
                    name="phone_number"
                    value={form.phone_number}
                    onChange={onChange}
                    readOnly={!editing}
                    pattern="^[0-9+\-()\s]{7,15}$"
                  />
                </div>
              </div>
            </div>
          </form>
        </div>
      </div>
      <div className="alert alert-info mt-3">
        <small>
          Note: After saving changes, the admin is notified immediately with the exact fields that changed.
        </small>
      </div>
    </div>
  );
};

export default Profile;
