import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import api from '../../utils/api';

const AdminMembers = () => {
  const { user } = useAuth();
  const [members, setMembers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMembers();
  }, []);

  const fetchMembers = async () => {
    try {
      const response = await api.get('/members');
      setMembers(response.data.data || []);
    } catch (error) {
      console.error('Error fetching members:', error);
    } finally {
      setLoading(false);
    }
  };

  if (user?.role !== 'admin') {
    return (
      <div className="alert alert-danger">
        <h4>Access Denied</h4>
        <p>You don't have permission to access this page.</p>
      </div>
    );
  }

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h1>Manage Members</h1>
          <p className="text-muted mb-0">Manage Chama Smart members and their roles</p>
        </div>
        <button className="btn btn-primary">
          Add New Member
        </button>
      </div>

      {loading ? (
        <div className="text-center py-4">
          <div className="spinner-border" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      ) : (
        <div className="card">
          <div className="card-header">
            <h5 className="mb-0">Current Members ({members.length})</h5>
          </div>
          <div className="card-body">
            {members.length === 0 ? (
              <div className="text-center py-4">
                <p className="text-muted">No members found.</p>
              </div>
            ) : (
              <div className="table-responsive">
                <table className="table table-hover">
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>Name</th>
                      <th>Role</th>
                      <th>Status</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {members.map((member) => (
                      <tr key={member.id}>
                        <td>{member.id}</td>
                        <td>{member.name}</td>
                        <td>
                          <span className={`badge ${
                            member.role === 'Chairman' ? 'bg-primary' :
                            member.role === 'Secretary' ? 'bg-info' :
                            member.role === 'Treasurer' ? 'bg-success' :
                            'bg-secondary'
                          }`}>
                            {member.role}
                          </span>
                        </td>
                        <td>
                          <span className="badge bg-success">Active</span>
                        </td>
                        <td>
                          <div className="btn-group btn-group-sm">
                            <button className="btn btn-outline-primary">Edit</button>
                            <button className="btn btn-outline-secondary">View</button>
                            <button className="btn btn-outline-danger">Remove</button>
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </div>
      )}

      <div className="row mt-4">
        <div className="col-md-4">
          <div className="card">
            <div className="card-body text-center">
              <h5 className="text-primary">25</h5>
              <p className="text-muted mb-0">Total Members</p>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card">
            <div className="card-body text-center">
              <h5 className="text-success">23</h5>
              <p className="text-muted mb-0">Active Members</p>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card">
            <div className="card-body text-center">
              <h5 className="text-warning">2</h5>
              <p className="text-muted mb-0">Pending Members</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminMembers;
