import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useNotifications } from '../contexts/NotificationContext';

const Layout = ({ children }) => {
  const { user, logout } = useAuth();
  const { unreadCount } = useNotifications();
  const location = useLocation();
  const navigate = useNavigate();
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const menuItems = [
    { path: '/dashboard', label: 'Dashboard', icon: '🏠' },
    { path: '/chamas', label: 'Chamas', icon: '👥' },
    { path: '/contributions', label: 'Contributions', icon: '💰' },
    { path: '/loans', label: 'Loans', icon: '🏦' },
    { path: '/meetings', label: 'Meetings', icon: '📅' },
    { path: '/reports', label: 'Reports', icon: '📊' },
    { path: '/profile', label: 'Profile', icon: '👤' },
    { path: '/settings', label: 'Settings', icon: '⚙️' }
  ];

  return (
    <div className="d-flex vh-100">
      {/* Sidebar */}
      <div className={`bg-dark text-white sidebar ${sidebarCollapsed ? 'collapsed' : ''}`} 
           style={{ width: sidebarCollapsed ? '80px' : '250px', transition: 'width 0.3s' }}>
        <div className="p-3">
          <div className="d-flex align-items-center mb-4">
            <h4 className={`mb-0 ${sidebarCollapsed ? 'd-none' : ''}`}>Chama Smart</h4>
            <button 
              className="btn btn-sm btn-outline-light ms-auto"
              onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
            >
              {sidebarCollapsed ? '→' : '←'}
            </button>
          </div>

          <nav className="nav flex-column">
            {menuItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                className={`nav-link text-white d-flex align-items-center py-2 ${
                  location.pathname === item.path ? 'bg-primary rounded' : ''
                }`}
                style={{ textDecoration: 'none' }}
              >
                <span className="me-2">{item.icon}</span>
                {!sidebarCollapsed && <span>{item.label}</span>}
              </Link>
            ))}
          </nav>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-grow-1 d-flex flex-column">
        {/* Header */}
        <nav className="navbar navbar-expand-lg navbar-light bg-light border-bottom">
          <div className="container-fluid">
            <div className="d-flex align-items-center ms-auto">
              {/* Notifications */}
              <div className="dropdown me-3">
                <button 
                  className="btn btn-outline-secondary position-relative"
                  type="button" 
                  data-bs-toggle="dropdown"
                >
                  🔔
                  {unreadCount > 0 && (
                    <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
                      {unreadCount}
                    </span>
                  )}
                </button>
                <ul className="dropdown-menu dropdown-menu-end">
                  <li><h6 className="dropdown-header">Notifications</h6></li>
                  <li><span className="dropdown-item-text">No new notifications</span></li>
                </ul>
              </div>

              {/* User Menu */}
              <div className="dropdown">
                <button 
                  className="btn btn-outline-secondary dropdown-toggle d-flex align-items-center"
                  type="button" 
                  data-bs-toggle="dropdown"
                >
                  <span className="me-2">👤</span>
                  {user?.first_name}
                </button>
                <ul className="dropdown-menu dropdown-menu-end">
                  <li>
                    <Link className="dropdown-item" to="/profile">
                      👤 Profile
                    </Link>
                  </li>
                  <li>
                    <Link className="dropdown-item" to="/settings">
                      ⚙️ Settings
                    </Link>
                  </li>
                  <li><hr className="dropdown-divider" /></li>
                  <li>
                    <button className="dropdown-item" onClick={handleLogout}>
                      🚪 Logout
                    </button>
                  </li>
                </ul>
              </div>
            </div>
          </div>
        </nav>

        {/* Page Content */}
        <main className="flex-grow-1 p-4 overflow-auto">
          {children}
        </main>
      </div>
    </div>
  );
};

export default Layout;
